import { IonIcon } from "@ionic/react";
import React, { useEffect, useRef, useState } from "react";
import { camera } from "ionicons/icons";
import io from "socket.io-client";
import { usePhotoGallery } from "../../../../../hooks/usePhotoGallery";
// import { base64FromPath } from "@ionic/react-hooks/filesystem";

const chat_server: any = process.env.REACT_APP_CHAT_SERVER;
const socket: any = io(chat_server);

const base64Image: any =
  "data:image/jpeg;base64,";

const Base64Upload: React.FC<any> = ({
  info,
  room,
  saveInfo,
  sender = "customer",
}) => {
  const { photos, takePhoto, deletePhotos, deletePhoto } = usePhotoGallery();
  const [percent, setPercent] = useState(0);
  const fileInput = useRef<any>(null);

  const dataURLtoFile = (dataurl: any, filename: string) => {
    //Usage example:
    // var file = dataURLtoFile('data:text/plain;base64,aGVsbG8gd29ybGQ=','hello.txt');
    // console.log(file);
    let arr: any = dataurl.split(","),
      mime = arr[0].match(/:(.*?);/)[1],
      bstr = atob(arr[1]),
      n = bstr.length,
      u8arr = new Uint8Array(n);

    while (n--) {
      u8arr[n] = bstr.charCodeAt(n);
    }

    return new File([u8arr], filename, { type: mime });
  };

  //   const handleFileChange = (e: any) => {
  //     const file = dataURLtoFile(base64Image, "eminem.jpg");
  //     handleUpload(file);

  //     // handleUpload(e.target.files[0]);
  //   };

  const handleUpload = async (file: any) => {
    if (!file) {
      return;
    }
    socket.emit("start-upload", { name: file.name, room });

    socket.on("ready", (data: any) => {
      console.log("ready  >>>>", data);
    });

    const chunkSize = (1024 * 1024) / 4; // .25MB
    const chunks = Math.ceil(file.size / chunkSize);
    let i = 0;

    socket.off("more-data").on("more-data", (data: any) => {
      if (i > chunks - 1) {
        const { name: fileName } = file;

        /***
    const { user_id, provider_id, task_id } = data;
    // console.log("message >>>>", message)
    const newMessage = {
      task_id,
      user_id,
      provider_id,
      sender,
      // message,
      ...messageData,
    };         
         */
        const newData = {
          ...info,
          id: 0,
          sender,
          fileName,
          message: `${process.env.REACT_APP_CHAT_SERVER}/uploads/${fileName}`,
          message_type: "file",
        };
        console.log("newData  >>>>", newData);
        socket.emit("end-upload", newData, room);
        saveInfo(newData);
        fileInput.current.value = null;
        return false;
      }
      //   console.log("more-data  >>>>", data);
      const start = i * chunkSize;
      const end = start + chunkSize;
      const chunk = file.slice(start, end);
      socket.emit("upload-chunk", { name: file.name, chunk });
      i++;
      setPercent((i / chunks) * 100);
    });
  };

  const openCamera = () => {
    // takePhoto("msg_", CameraSource.Camera, true);
    takePhoto("msg_");
    // alert("Future feature");
  };

  useEffect(() => {
    deletePhotos();
  }, []);

  const getFileFromBlob = async (photo: any, filename: string) => {
    const response: any = await fetch(photo?.webviewPath + "");
    const blob: any = await response.blob();
    return new File([blob], "blob_" + filename);
  };
  useEffect(() => {
    if (photos?.length > 0) {
      let file = null;
      const photo: any = photos[0];

      alert("photos : " + JSON.stringify(photo));

      const { filepath, webviewPath } = photo;
      const filename = filepath?.substring(filepath?.lastIndexOf("/") + 1);

      if (webviewPath?.includes("blob")) {
        file = new File([webviewPath], "blob_" + filename);
      } else if (webviewPath?.includes("base")) {
        file = dataURLtoFile(webviewPath, "base_" + filename);
      } else {
        file = new File([filepath], "filepath_" + filename);
        // file = getFileFromBlob(photo, filename);
        alert("filepath >>>" + JSON.stringify(filepath));
      }
      handleUpload(file);

      alert("file : " + JSON.stringify(file));
      // alert("photos?.length : "+photos?.length)
      //   savePhoto(photos[0]);
      deletePhotos();
      //   deletePhoto(photos[0]);
    }
    // eslint-disable-next-line
  }, [photos]);

  return (
    <>
      <IonIcon
        icon={camera}
        slot="start"
        color="primary"
        className="cursor-pointer"
        // onClick={() => fileInput?.current?.click()}
        // onClick={handleFileChange}
        onClick={() => openCamera()}
      />
      {/* <input
        hidden
        type="file"
        onChange={handleFileChange}
        ref={fileInput}
        accept="image/png, image/gif, image/jpeg"
      /> */}
      {/* <button onClick={handleUpload}>Upload</button> */}
      <p style={{ color: "gold", position: "absolute" }}>{percent}%</p>
      {/* {console.log("fileUpload info: >>>>>>", info)} */}
    </>
  );
};

export default Base64Upload;
